/// <reference types="vite/client" />
// Allow importing CSS and common static assets from TypeScript files
// This prevents errors like: "Cannot find module or type declarations for side-effect import of './index.css'"

declare module "*.jpg";
declare module "*.svg";

declare module "*.css";
declare module "*.scss";
declare module "*.sass";
declare module "*.less";
declare module "*.styl";

declare module "*.png";
declare module "*.jpg";
declare module "*.jpeg";
declare module "*.gif";
declare module "*.webp";
declare module "*.avif";

declare module "*.svg";
